import React, { useEffect } from 'react';
import { Route, createBrowserRouter, createRoutesFromElements, RouterProvider, } from "react-router-dom";
import './App.css';
import './header';
import './main';
import './ReservationsPage';
import './ResConfirmPage';
import './footer';


// components
import Hero from "./components/Hero";
import ReservationsPage from './ReservationsPage';
import ResConfirmPage from './ResConfirmPage';

import RootLayout from './layouts/RootLayout';
import { useFormContext } from './store/FormContext';

function App() {
  const { form } = useFormContext();
  useEffect(() => {
    fetch('./APIs/apiMockup.js')
    .then(response => navigate("/ResConfirm"))
  })

const formProps = {
  name: form.name,
  date: form.date,
  time: form.time,
  guests: form.numberOfGuests,
  occasion: form.occasion,
};

const router = createBrowserRouter(
  createRoutesFromElements(
    <Route path='/' element={<RootLayout />}>
      <Route index element={<Hero />} />
      <Route path="ReservationsPage" element={<ReservationsPage />} />
      <Route
       path="/ResConfirmPage"
       element={<ResConfirmPage {...formProps} />}
       />
    </Route>
  )
)
  return (
    <RouterProvider router={router} />
  );
}

export default App;
